// Load defaults
var vrms = global.get("vrms");
var solar = global.get("solar");
var use = global.get("use");
var excess = global.get("excess");
var grid = global.get("grid");
var battery = global.get("battery");
var max_charge_current = global.get("max_charge_current");
var min_charge_current = global.get("min_charge_current");
var charge_current = max_charge_current;
var mode = global.get("mode");
var state = global.get("state");
var openESEV_power = global.get("openESEV_Power");

// Get VRMS voltage : default 230v
if (msg.topic === "vrms") {
    vrms = msg.payload;
    global.set("vrms",vrms);
}

// Get Solar PV gen & Convert to current (A)
if (msg.topic === "solar") {
    solar = msg.payload / vrms;
    global.set("solar",solar)
}

// Get on-site consumption & Convert to current (A)
if (msg.topic === "use") {
    use = msg.payload / vrms;
    global.set("use",use)
}

// Get Grid Watts & Convert to current (A)
if (msg.topic === "grid") {
    grid = msg.payload / vrms;
    global.set("grid",grid)
}
// Get House Battery Watts & Convert to current (A)
if (msg.topic === "battery") {
    battery = msg.payload / vrms;
    global.set("battery",battery)
}

// Normal Charge
if (mode == 1){
    charge_current = max_charge_current;
}

// Eco Mode
    //need if () where battery is not discharging 
    if (battery < 0) {//battery discharging
        excess = (solar - battery);
    }
    else {
        excess = solar; 
    }
if (mode == 2){
    if (excess >= min_charge_current){
        charge_current = excess;
    }
    else {
        // If no excess current charge current is 0 (EVSE will reduce charge to minimum charge rate (6A default)
        charge_current = 0;//min_charge_current; 
    }
}

charge_current=Math.round(charge_current);

return [
    {payload: charge_current}, 
    {payload: mode}, 
    {payload: state}, 
    {payload: "charge_current:" + charge_current + " excess:" + excess + " solar:"+ solar + " use:"+ use + " grid:"+ grid + " battery:" + battery}
    ];

