var vrms = global.get("vrms");
var solar = global.get("solar");
var use = global.get("use");
var excess = global.get("excess");
var grid = global.get("grid");
var battery = global.get("battery");
var max_charge_current = global.get("max_charge_current");
var min_charge_current = global.get("min_charge_current");
var last_charge_current = global.get("last_charge_current");
var mode = global.get("mode");
var state = global.get("state");
var openESEV_power = global.get("openESEV_Power"); 
var charge_current = msg.payload;
var this_charge_current = msg.payload;


//Consumption Limiting
 if (mode == 1){ // normal mode

    // if amps are too high start to reduce them
    if (last_charge_current == 32 && grid >98){                    // -> 98        
        charge_current = 16;                 
    }else if (last_charge_current == 16 && grid >96){              // -> 96
        charge_current = 6;
    }else if (last_charge_current == 6 && grid >96){               // -> 96
        charge_current = 0;
    }else if (last_charge_current === 0 && grid >96){              // -> 96
        charge_current = 0;
    }
    // now maintain amps below maximum
    else if (last_charge_current == 16 && grid >82 && grid <98){        //16  82 <> 98
        charge_current = 16;
    }else if (last_charge_current == 6 && grid >92 && grid <98){   //6   92 <> 98
        charge_current = 6; 
    }else if (last_charge_current === 0 && grid >92){              //0      -> 92
        charge_current = 0; 
    }
    // now increase amps as overall current reduces
    else if (last_charge_current === 0 && grid >82 && grid <92){        //0    82 <> 92
        charge_current = 6;
    }else if (last_charge_current === 0 && grid >66 && grid <82){  //0    66 <> 82
        charge_current = 16;
    }else if (last_charge_current === 0 && grid <66){              //0      <- 66
        charge_current = 32;
    }else if (last_charge_current == 6 && grid >82 && grid <92){   //6    82 <> 92
        charge_current = 6;
    }else if (last_charge_current == 6 && grid >66 && grid <82){   //6    66 <> 82
        charge_current = 16;
    }else if (last_charge_current == 6 && grid <66){               //6      <- 66
        charge_current = 32;
    }else if (last_charge_current == 16 && grid >66 && grid <82){  //16   66 <> 82
        charge_current = 16;
    }else if (last_charge_current == 16 && grid <66){              //16     <- 66
        charge_current = 32;
    }
}

if (mode == 2){ // eco mode

    if (last_charge_current >= 0 && grid >92 && grid <98){                           //0    92 <> 98
        charge_current = 0;
    }else if (last_charge_current >= charge_current && grid >82 && grid <92){        //>    82 <> 92
        charge_current = this_charge_current;
    }else if (last_charge_current >= charge_current && grid >66 && grid <92){        //>    66 <> 92
        charge_current = this_charge_current;
    }

    if (last_charge_current == charge_current && grid >98){                          //=    -> 98
        charge_current = 0;
    }else if (last_charge_current => 6 && grid >82 && grid <98){                     //=    82 <> 98

        if ((99 - grid) < this_charge_current){
        charge_current = (99 - grid);          
        }else{
            charge_current = this_charge_current
        }
    }

    if (charge_current <= 0){
        charge_current = 0;
    }

}


charge_current=Math.round(charge_current);
global.set("last_charge_current",charge_current)

return [{payload: charge_current},
{payload:"grid:" + grid + " charge_current:" + charge_current + ": last_charge_current:" + last_charge_current}];
